using System;
using System.Collections.Generic;
using System.IdentityModel.Tokens.Jwt;
using System.Security.Cryptography.X509Certificates;
using Microsoft.IdentityModel.Tokens;
using Newtonsoft.Json.Linq;

namespace CodeSnips.TokenValidation
{
    // example of how to use ECDSA certs
    public class ECDsaCertSignatureValidation
    {
        public static void Run()
        {
            var jwt = "..upRYO2Ycmx4ESVpNUjobRNOF4v_r9qMP4gU4mReeR1mGFL8Gw8JBMnlDKN6n9rXeIP3Jkdjbpb0IzBB4JgSzOQ";

            SecurityToken token = null;
            var tokenHandler = new JwtSecurityTokenHandler();
            var validationParameters = new TokenValidationParameters
            {
                IssuerSigningKeyResolver = (t, securityToken, kid, tvp) => 
                {
                    var jwtToken = new JwtSecurityToken(t);
                    var x5c = jwtToken.Header["x5c"];
                    var cert = GetCert(x5c);
                    var ecDsaKey = cert.GetECDsaPublicKey();
                    return new List<SecurityKey> { new ECDsaSecurityKey(ecDsaKey) };
                },
                IssuerSigningKeyValidator = (key, securityToken, tvp) =>
                {
                    // validate key and return true or false
                    // currently not working in 5.1.3, will be fixed in 5.2.0
                    // validate in IssuerSigningKeyResolver
                    return true;
                },
                ValidateAudience = false,
                ValidateIssuer = false,
                ValidateLifetime = false
            };

            var principal = tokenHandler.ValidateToken(jwt, validationParameters, out token);
        }


        public static X509Certificate2 GetCert(object x5c)
        {
            if (x5c is string x509Data)
            {
                return new X509Certificate2(Convert.FromBase64String(x509Data));
            }

            if (x5c is JArray jarray)
            {
                var cerData = jarray[0];
                if (cerData.Type != JTokenType.String)
                    throw new NotSupportedException($"jarray[0] must be the cert that signed the token and must be a string as base64encoded data: '{cerData}'.");

                return new X509Certificate2(Convert.FromBase64String(cerData.ToString()));
            }

            throw new NotSupportedException($"x5c is not understood: {x5c}");
        }
    }
}
